import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, Button } from 'react-native';
import { BarCodeScanner } from 'expo-barcode-scanner';

export default function App() {
  const [hasPermission, setHasPermission] = useState(null);
  const [scanned, setScanned] = useState(false);
  const [scannedData, setScannedData] = useState(null);

  useEffect(() => {
    (async () => {
      const { status } = await BarCodeScanner.requestPermissionsAsync();
      setHasPermission(status === 'granted');
    })();
  }, []);

  const handleBarCodeScanned = ({ type, data }) => {
    setScanned(true);
    setScannedData({ type, data });
  };

  const resetScanner = () => {
    setScanned(false);
    setScannedData(null);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Escanea un Código QR</Text>
      {hasPermission === null ? (
        <Text>Esperando permiso de cámara...</Text>
      ) : hasPermission === false ? (
        <Text>Acceso a la cámara denegado.</Text>
      ) : (
        // Condición para renderizar BarCodeScanner solo en dispositivos móviles
        Platform.OS === 'web' ? (
          <Text>BarCodeScanner no es compatible en la web.</Text>
        ) : (
          <View style={styles.scannerContainer}>
            <BarCodeScanner
              onBarCodeScanned={scanned ? undefined : handleBarCodeScanned}
              style={styles.scanner}
            />
            {scanned && (
              <View style={styles.scanData}>
                <Text>Tipo de Código: {scannedData.type}</Text>
                <Text>Datos: {scannedData.data}</Text>
                <Button title="Escanear de nuevo" onPress={resetScanner} />
              </View>
            )}
          </View>
        )
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  scannerContainer: {
    width: '100%',
    height: '50%',
    position: 'relative',
  },
  scanner: {
    width: '100%',
    height: '100%',
  },
  scanData: {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    padding: 16,
  },
});
